import { describe, expect, it } from 'vitest'
import render from './render'

describe('render', () => {
	const data = [
		'....',
		'....',
		'.X-.',
		'.-X.',
		'.-.X',
		'..-X',
		'.XXX',
		'----',
		'...X',
		'.XX.',
		'XX..',
	]
	const result =
		'data:image/png;base64,'
	it('should create an image', () => {
		const base64 = render(data, 50, [
			[0, 0, 50],
			[0, 0, 0],
		])
		expect(base64).toBe(result)
	})
})
